mapper.select:
SELECT COALESCE(MAX(file_serial_number),0)+1 AS file_serial_number
	FROM file
WHERE file_no = #{file_no}

mapper.insert:

INSERT INTO file(
	  file_no
	, file_serial_number
	, origin_name
	, physical_name
	, file_url
	, size
	, extension
) values (
	  #{file_no}
	  #{file_serial_number}
	, #{origin_name}
	, #{physical_name}
	, #{file_url}
	, #{size}
	, #{extension}
)
